import requests
import sys
import json
import os
from datetime import date

sjs = 'https://prods.cse.wustl.edu/js/server.js'
mdtmp = '/tmp/pscript.md'

fmt_ = '''
<img src="SRC" style="height: 9em;">


<div style="font-family: Times; font-size: 20px; text-align: justify;">
**DATE**

To: SEMAIL<br />
CC: AEMAIL

Dear SNAME,

BODY

---<br />
Doctoral Studies Committee<br />
Computer Science & Engineering<br />
Washington University in St. Louis

</div>
'''


def main(argv):
    if len(argv) < 2:
        print("Usage: %s admin.password out.directory" % argv[0])
        return

    fmt = fmt_.replace('SRC', img_str())
    fmt = fmt.replace('DATE', date.today().strftime('%B %-d, %Y'))
    admpass, outdir = argv[1], argv[2]

    server = requests.get(sjs).content.decode('utf-8')
    server = server.split('"')[1]

    resp = requests.post(server + 'admin',
                         json={'op': 'getlists',
                               'pswd': admpass})
    resp = resp.json()['lists']
    students = resp['dblist']['students']
    advisors = resp['dblist']['advisors']
    emails = resp['emails']

    resp = requests.post(server + 'admin',
                         json={'op': 'getstate',
                               'pswd': admpass})

    scpass = resp.json()['pswds']['scribe']

    read_req = {'op': 'read', 'uid': 'scribe', 'pswd': scpass}

    csv = open(outdir + '/0list.csv', 'wt')
    emails['none'] = ''

    for j, (k, v) in enumerate(students.items()):
        fname = v['name'].split(',')[-1]
        semail = v['name'] + ' ('+emails[k]+')'
        aid = v['advisor']
        if aid != 'none':
            aemail = advisors[aid]['name'] + ' ('+emails[aid]+')'
        else:
            aemail = ''

        read_req['key'] = 'prod_' + k
        body = requests.post(server, json=read_req).json()['val']
        try:
            body = json.loads(body)
        except:
            continue

        pdfname = body[0][1] + '_' + k + '.pdf'
        csv.write(','.join([pdfname, emails[k], emails[aid]]) + '\n')

        outstr = fmt.replace('SNAME', fname).replace('SEMAIL', semail)
        outstr = outstr.replace('AEMAIL', aemail).replace('BODY', body[1][1])
        with open(mdtmp, 'w') as f:
            f.write(outstr)

        os.system(
            'pandoc -f markdown ' + mdtmp +
            ' -t html5 -V margin-left=2cm -V margin-right=2cm' +
            ' -V margin-bottom=2cm -V margin-top=1cm' +
            ' -o ' + outdir + '/' + pdfname
        )

    csv.close()


# Mckelvey Logo
def img_str():
    return "data:image/gif;base64,"


main(sys.argv)
